#define N 16

SYCL_EXTERNAL std::array<int, 16> SyclBubbleSort(
        std::array<int, 16> x
){

        // Input registers
        int a[N];

        #pragma unroll
        for (int i = 0; i < N; i++)
                a[i] = x[i];

        // bubble sort
        int tmp = 0;

        #pragma unroll
        for (int i = 0; i < N; ++i) {
            #pragma unroll
            for (int j = 0; j < N - i - 1; ++j) {
                if (a[j] > a[j + 1]) {
                    tmp = a[j];
                    a[j] = a[j + 1];
                    a[j + 1] = tmp;
                }
            }
        }

        // Output registers
        std::array<int, 16> a0;

        #pragma unroll
        for (int i = 0; i < N; i++)
                a0[i] = a[i];

        return a0;
}

