/**	#1
 * serial primitive for Intel Intrinsic:
 * _mm512_setzero_epi32
 */
template<typename T, int B>
fpvec<T,B> setzero() {
	auto reg = fpvec<T,B>{};
	Type zero = 0;

	#pragma unroll
	for(int offset=0; offset < outerUnroll; offset++) {
		#pragma unroll
		for (int i=0; i<innerUnroll; i++) {
			reg.elements[(offset*innerUnroll)+i] = zero;
		}
	}	
	return reg;
} 

