/**	#20
* Own function to create the overflow_correction_mask
*/
template<typename T, int B>
fpvec<T,B> createOverflowCorrectionMask(T oferflowUnsigned) {
	auto reg = fpvec<T,B>{};
	const int overflow = (B/sizeof(T)) - oferflowUnsigned;
	Type one = 1;
	Type zero = 0;

	#pragma unroll
	for(int offset=0; offset < outerUnroll; offset++) {
		#pragma unroll
		for (int i=0; i<innerUnroll; i++) {
			if (((offset*innerUnroll)+i)<overflow) {
				reg.elements[(offset*innerUnroll)+i] = one;
			}
			else {
				reg.elements[(offset*innerUnroll)+i] = zero;
			}		
		}
	}	

	return reg;
} 

/**	#21
* Own function to create the cutlow_mask
*/
template<typename T, int B>
fpvec<T,B> createCutlowMask(T cutlowUnsigned) {
	auto reg = fpvec<T,B>{};
	const int cutlow_const = (B/sizeof(T)) - cutlowUnsigned;
	Type one = 1;
	Type zero = 0;

	#pragma unroll
	for(int offset=0; offset < outerUnroll; offset++) {
		#pragma unroll
		for (int i=0; i<innerUnroll; i++) {
			if ((offset*innerUnroll)+i<cutlow_const) {
				reg.elements[(offset*innerUnroll)+i] = zero;
			}
			else {
				reg.elements[(offset*innerUnroll)+i] = one;
			}
		}			
	}
	return reg;
} 

